import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getEventById } from '../services/events';

const EventDetail = () => {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const data = await getEventById(id);
        setEvent(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch event details. Please try again later.');
        setLoading(false);
      }
    };

    fetchEvent();
  }, [id]);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return <div>Loading event details...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!event) {
    return <div>Event not found.</div>;
  }

  return (
    <div className="event-detail">
      <h2>{event.title}</h2>
      <p className="description">{event.description}</p>
      
      <div className="event-info">
        <p><strong>Start Date:</strong> {formatDate(event.startDate)}</p>
        <p><strong>End Date:</strong> {formatDate(event.endDate)}</p>
        <p><strong>Location:</strong> {event.location}</p>
        <p><strong>Available Spots:</strong> {event.maxAttendees - event.currentAttendees} / {event.maxAttendees}</p>
      </div>

      <div className="actions">
        <Link to={`/events/${event.id}/register`}>
          <button className="btn">Register for this Event</button>
        </Link>
      </div>
    </div>
  );
};

export default EventDetail;
