import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getEventById } from '../services/events';
import { register } from '../services/registrations';

const RegistrationForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
  });

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const data = await getEventById(id);
        setEvent(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch event details. Please try again later.');
        setLoading(false);
      }
    };

    fetchEvent();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      await register(id, formData);
      setSuccess(true);
      setTimeout(() => {
        navigate(`/events/${id}`);
      }, 3000);
    } catch (err) {
      setError('Registration failed. You might already be registered for this event.');
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error && !success) {
    return <div className="error">{error}</div>;
  }

  if (success) {
    return (
      <div className="success-message">
        <h2>Registration Successful!</h2>
        <p>You have successfully registered for {event?.title}.</p>
        <p>Redirecting to event page...</p>
      </div>
    );
  }

  return (
    <div>
      <h2>Register for {event?.title}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn">Register</button>
      </form>
    </div>
  );
};

export default RegistrationForm;
