import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getEventById } from '../services/events';

const EventDetail = () => {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const data = await getEventById(id);
        setEvent(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch event details. Please try again later.');
        setLoading(false);
      }
    };

    fetchEvent();
  }, [id]);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        <p>Loading event details...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!event) {
    return <div className="error">Event not found.</div>;
  }

  // Calculate if event is upcoming
  const isUpcoming = new Date(event.startDate) > new Date();
  
  // Calculate available spots
  const availableSpots = event.maxAttendees - event.currentAttendees;
  const spotsAvailable = availableSpots > 0;

  return (
    <div className="event-detail">
      <div className="event-header">
        {isUpcoming && (
          <span className="event-badge upcoming">Upcoming</span>
        )}
        <h2>{event.title}</h2>
        <div className="event-organizer">
          <span>Organized by: </span>
          <strong>{event.organizerName || 'Anonymous'}</strong>
        </div>
      </div>
      
      <div className="event-body">
        <div className="description">
          <h3>About this event</h3>
          <p>{event.description}</p>
        </div>
        
        <div className="event-sidebar">
          <div className="event-info">
            <div className="info-item">
              <svg xmlns="http://www.w3.org/2000/svg" className="icon" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd" />
              </svg>
              <div>
                <h4>Start Date</h4>
                <p>{formatDate(event.startDate)}</p>
              </div>
            </div>
            
            <div className="info-item">
              <svg xmlns="http://www.w3.org/2000/svg" className="icon" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd" />
              </svg>
              <div>
                <h4>End Date</h4>
                <p>{formatDate(event.endDate)}</p>
              </div>
            </div>
            
            <div className="info-item">
              <svg xmlns="http://www.w3.org/2000/svg" className="icon" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
              </svg>
              <div>
                <h4>Location</h4>
                <p>{event.location}</p>
              </div>
            </div>
            
            <div className="info-item">
              <svg xmlns="http://www.w3.org/2000/svg" className="icon" viewBox="0 0 20 20" fill="currentColor">
                <path d="M9 6a3 3 0 11-6 0 3 3 0 016 0zM17 6a3 3 0 11-6 0 3 3 0 016 0zM12.93 17c.046-.327.07-.66.07-1a6.97 6.97 0 00-1.5-4.33A5 5 0 0119 16v1h-6.07zM6 11a5 5 0 015 5v1H1v-1a5 5 0 015-5z" />
              </svg>
              <div>
                <h4>Capacity</h4>
                <p className={spotsAvailable ? '' : 'full'}>
                  {spotsAvailable 
                    ? `${availableSpots} spots available (${event.currentAttendees}/${event.maxAttendees})` 
                    : 'Event is full'}
                </p>
              </div>
            </div>
          </div>
          
          <div className="actions">
            {isUpcoming && spotsAvailable ? (
              <Link to={`/events/${event.id}/register`} className="btn btn-primary">
                Register Now
              </Link>
            ) : isUpcoming ? (
              <button className="btn btn-disabled" disabled>
                Event is Full
              </button>
            ) : (
              <button className="btn btn-disabled" disabled>
                Event has Ended
              </button>
            )}
            
            <Link to="/events" className="btn btn-secondary">
              Back to Events
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetail;
